SAMPLE_TEST_DB = "../ressources/test_data.db"
BEATMAPSET_QUERY = 'insert into beatmapset values (?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)'
BEATMAP_QUERY = 'insert into beatmap values (?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?,?)'
USER_QUERY = 'insert into user values (?,?,?)'
